import UIKit

class FashionProductConfigurator {
    
    // MARK: Configuration
    class func viewcontroller() -> FashionProductViewController {
        let viewController = FashionProductViewController(nibName: "FashionProductViewController", bundle: nil)
        
        let presenter = FashionProductPresenter()
        presenter.output = viewController
        
        let interactor = FashionProductInteractor()
        interactor.output = presenter
        
        let router = FashionProductRouter()
        router.viewController = viewController

//        //Data passing
//        router.dataSource = interactor
        
        viewController.output = interactor
        viewController.router = router
        
        return viewController
    }
}

