    class func viewcontroller() -> FeaturedProductViewController {
//        let viewController = FeaturedProductViewController(nibName: "FeaturedProductViewController", bundle: nil)
        let viewController = FeaturedProductViewController(itemInfo: "FOLLOWING")
        
        let presenter = FeaturedProductPresenter()
        presenter.output = viewController
        
        let interactor = FeaturedProductInteractor()
        interactor.output = presenter
        
        let router = FeaturedProductRouter()
        router.viewController = viewController

//        //Data passing
//        router.dataSource = interactor
        
        viewController.output = interactor
        viewController.router = router
        
        return viewController
    }
}

